import React from 'react';
import { Form, Input, Switch, Button } from 'antd';
import { Controller, useForm } from "react-hook-form";
import MyInput from '../../../components/AntFields';
import IntlMessages from "../../../util/IntlMessages";

const formItemLayout = {
  labelCol: {
    sm: { span: 24 },
    md: { span: 7 },
    lg: { span: 3 },
  },
  wrapperCol: {
    sm: { span: 24 },
    md: { span: 17 },
    lg: { span: 21 },
  },
}


const FormClient = ({ initialValues, onSubmit }) => {
  const { handleSubmit, control, formState, errors } = useForm({ defaultValues: initialValues });
  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <MyInput
        AntComponent={Input}
        formState={formState}
        errors={errors}
        name="name"
        label={<IntlMessages id="Name" />}
        type="text"
        rules={{ required: <IntlMessages id="User" /> }}
        placeholder={`Nombre`}
        control={control}
        formItemLayout={formItemLayout}
      />
      <MyInput
        AntComponent={Input}
        formState={formState}
        errors={errors}
        name="username"
        label="Usuario"
        type="text"
        rules={{ required: `El campo usuario es requerido` }}
        placeholder={`Usuario`}
        control={control}
        formItemLayout={formItemLayout}
      />
      <MyInput
        AntComponent={Input}
        formState={formState}
        errors={errors}
        name="password"
        label="Contraseña"
        type="password"
        rules={{ required: `El campo contraseña es requerido` }}
        placeholder={`Contraseña`}
        control={control}
        formItemLayout={formItemLayout}
      />
      <MyInput
        AntComponent={Switch}
        formState={formState}
        errors={errors}
        name="admin"
        label="Empleado"
        control={control}
        formItemLayout={formItemLayout}
      />
      <Button htmlType="submit" block type="primary">Agregar Cliente</Button>
    </Form>
  );
};

export default FormClient;
